func TestAccountStateDB_ProcessContract(t *testing.T) {
	log.InitLogger(log.LvlDebug)
	transactionJson := "{\"TxData\":{\"nonce\":\"0x5\",\"to\":\"0x00000000000000000000000000000000000000000018\",\"hashlock\":null,\"timelock\":\"0x0\",\"value\":\"0x2540be400\",\"fee\":\"0x424ad340\",\"gasPrice\":\"0x1\",\"gas\":\"0x12\",\"extraData\":\"\"},\"Wit\":{\"r\":\"0x911be4b1426a306aa67e96c69edec4706f9b970d776c73a451e33ab50b7eb60a\",\"s\":\"0x797761d0a881cd8a08ba746dc949bb583e65153575941b093d4510422133e908\",\"v\":\"0x38\",\"hashkey\":\"0x\"}}"
	var tx model.Transaction
	err := json.Unmarshal([]byte(transactionJson), &tx)
	if err != nil {
		log.Info("TestAccountStateDB_ProcessContract", "err", err)
	}
	log.Info("processContract", "tx", tx)

	block := model.Block{}



	db := ethdb.NewMemDatabase()
	sdb := NewStateStorageWithCache(db)
	processor, _ := NewAccountStateDB(common.Hash{}, sdb)
	processor.ProcessContract(&tx, &block, true)

}